mapper.update:
update Gallery
set g_content = #{G_CONTENT},
    g_tag1    = #{g_TAG1},
    g_edit    = sysdate,
    g_status  = #{G_STATUS}
where g_no = #{G_NO}

update gallery
set g_readcount = g_readcount + 1
where g_no = #{G_NO}

update gallery
set g_sales = g_sales + 1
where g_no = #{G_NO}

update gallery
set g_like = g_like + 1
where g_no = #{G_NO}

update gallery
set g_status = 2
where g_no = #{G_NO}

